export default function Petitiondetails(props) {
    const classes = useStyles();
    const bull = <span className={classes.bullet}>â€¢</span>;
    const [allroom, setAllroom] = useState([]);

    const { id } = useParams();
    useEffect(() => {
        //const petitiondetails=(id) =>{

        axios("/petition/petitions/" + id)
            .then(response => {
                console.log("hi" + response.data)
                setAllroom(response.data);
            })
            .catch(error => {
                console.log('Error getting fake data: ' + error);
            })

    }, [id]);

    const PetitionNote = (id) => {
        axios.post(`/petition/delete-petition/${id}`)
            .then(() => {
                setAllroom(
                    allroom.filter((row) => {
                        return row.id != id;
                    })
                );
            });
    };

    const Solve = (id) => {
        axios.post(`/petition/solve-petition/${id}`, {

            "Status": true

        }).then((response) => {
            window.location.href = `/notedetails/${id}`
            console.log(response);
        })
    };

    console.log(allroom)
    return (
        <div className="container ">
            <div className={classes.DateMove}>
                <Datetoday

                />
                <h5 className={classes.Hederpettition} id="newannouncetitle">
                    Petition Details
                </h5>
            </div>

            {allroom.map((row) => {
                return (
                    <Card className={classes.Card} variant="outlined">
                        {row.petition.map((x) => {
                            return (
                                <Table
                                    aria-label="caption table">
                                    <TableHead >
                                        <TableRow>
                                            <TableCell className={classes.heder}>
                                                <h4> {x.title} </h4>
                                            </TableCell>
                                            <TableCell>
                                                <IconButton aria-label="delete">
                                                    <EmojiObjectsIcon
                                                        onClick={() => { Solve(id) }}
                                                    />
                                                </IconButton>
                                            </TableCell>
                                            <TableCell>
                                                <IconButton aria-label="delete">
                                                    <DeleteIcon
                                                        onClick={() => { PetitionNote(id) }}
                                                    />
                                                </IconButton>
                                            </TableCell>
                                        </TableRow>
                                        <TableRow>
                                            <TableCell className={classes.heder}>
                                                <h6>{x.userFirstName}</h6>
                                                <h6>Room{x.roomNumber}</h6>
                                            </TableCell>
                                            <TableCell className={classes.heder}>
                                                {moment(x.petitionDate).format("L")}
                                            </TableCell>
                                            <TableCell >
                                                {x.statusInfo}
                                            </TableCell>
                                        </TableRow>
                                    </TableHead>
                                    <TableBody>

                                        <div className="container ">
                                            <Table>
                                                <TableRow className={classes.heder}>
                                                    <h5> {x.description}</h5>
                                                </TableRow>
                                                <TableRow>
                                                    <img className={classes.media}
                                                        src={x.image} />
                                                </TableRow>
                                            </Table>
                                        </div>
                                    </TableBody>
                                </Table>
                            )
                        })}
                    </Card>
                )
            })}
        </div>
    );
}
