import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import ButtonBase from '@material-ui/core/ButtonBase';
import "./../../Css/Announcement/general.css"

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    margin: theme.spacing(1),
  },
  paper: {
    padding: theme.spacing(1),
    margin: 'auto',
    maxWidth: 340,
    height: 80
  },
  image: {
    width: 100,
    height: 63,
    position: 'relative',



  },
  img: {
    // margin: 'auto',
    // display: 'block',
    // maxWidth: '100%',
    // maxHeight: '100%',
    width: 100,
    height: 62,
    objectFit: 'cover',
    borderRadius: 8

  },
}));

export default function ComplexGrid(props) {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <Paper className={classes.paper}>
        <Grid container spacing={2}>
          <Grid item>
            <ButtonBase className={classes.image}>
              <img className={classes.img} src="https://www.simplilearn.com/ice9/free_resources_article_thumb/COVER-IMAGE_Digital-Selling-Foundation-Program.jpg" />
            </ButtonBase>
          </Grid>
          <Grid item xs={12} sm container>
            <Grid item xs container direction="column" spacing={2}>
              <Grid item xs>
                {/* <Typography gutterBottom variant="subtitle1">  
                </Typography> */}
                {/* <Typography variant="body2" color="textSecondary"> */}
                 <p id="generalcelldate" >18/04/2021</p> 
                {/* </Typography> */}
                {/* <Typography gutterBottom variant="subtitle1"> */}
                 <p id="generalcelltitle" >General News2 </p> 
                {/* </Typography> */}
              </Grid>
              <Grid item>
                <Typography variant="body2" style={{ cursor: 'pointer' }}>
                  {props.remove}
                </Typography>
              </Grid>
            </Grid>
            <Grid item>

            </Grid>
          </Grid>
        </Grid>
      </Paper>
    </div>
  );
}
