const BootstrapInput = withStyles((theme) => ({
    root: {
      'label + &': {
        marginTop: theme.spacing(3),
      },
    },
    input: {
      borderRadius: 4,
      position: 'relative',
      backgroundColor: theme.palette.background.paper,
      border: '1px solid #ced4da',
      fontSize: 16,
      padding: '10px 26px 10px 12px',
      transition: theme.transitions.create(['border-color', 'box-shadow']),
      // Use the system font instead of the default Roboto font.
      fontFamily: [
        '-apple-system',
        'BlinkMacSystemFont',
        '"Segoe UI"',
        'Roboto',
        '"Helvetica Neue"',
        'Arial',
        'sans-serif',
        '"Apple Color Emoji"',
        '"Segoe UI Emoji"',
        '"Segoe UI Symbol"',
      ].join(','),
      '&:focus': {
        borderRadius: 4,
        borderColor: '#80bdff',
        boxShadow: '0 0 0 0.2rem rgba(0,123,255,.25)',
      },
    },
  }))(InputBase);

export default function Billpage({isOpened}) {
    const classes = useStyles();
    const [bill, setBill] = useState([]);
    const [search, setSearch] = useState("")
    const [dropdown, setDropdown] = useState([])
    const [ building, setBuilding] = useState([])
    const dateFormatter = date => {
      // return moment(date).unix();
      return moment(date).format('DD-MM-YY');
    };

    React.useEffect(() => {
        const fetchData = () =>{
         axios.get('/bill/bills/1/date/2021-09-16')
         .then(r => 
          setBill(r.data))
        }
        fetchData()
      }, [])
        console.log(bill)

    React.useEffect(() => {
        const fetchData = () =>{
             axios.get('/dropdown/buildings')
             .then(r => 
              setDropdown(r.data))
            }
            fetchData()
        }, [])


    const Closebill =()=>{
      axios.post("/bill/closed-bill",{
      }).then(() => {
      setBill([
        {
          "BillIds": [1,2,3]
        },
      ]);    
      window.location.href = '/createfeetype';
    });
  };


    return (

        <ScrollView>
            <div className={isOpened ? classes.scrollspace36 : classes.scrollspace}>
                <div>
                    <div className={classes.frame}>
                        <Datetoday />
                        <h5 className={classes.Billheadd} id="newannouncetitle">  Bill </h5>   

        <div className="row align-items-start">       

         <div className="col">    
              <Paper component="form" className={classes.Searchstyle}>
              <BootstrapInput
                  type="text"
                  size="small"
                  variant="outlined" 
                  //label="Date"     
                  placeholder="search"
                  onChange={(e)=>{
                    setSearch(e.target.value);
                  }}                  
                />
            </Paper>
          </div>                      
          <div className="col">    
              <Paper component="form" className={classes.Searchbuilding}>
              <BootstrapInput
                  type="date"
                  size="small"
                  variant="outlined" 
                  label="Date"     
                  placeholder="Date"

                />
            </Paper>
          </div>

          <div className="col">    
            <Paper component="form" className={classes.Searchbuilding2}>
              <NativeSelect
               className={classes.dropdown}
                id="demo-customized-select-native"
                value={building}
                //onChange={handleChange}
                  onChange={(e)=>{
                    setBuilding(e.target.value);
                  }}    
                input={<BootstrapInput />}
              >
                {dropdown.map((val)=>(
                  <option aria-label="None" value="" >
                    {val.text}
                  </option>   
                 ))}      
              </NativeSelect>
            </Paper>
          </div>

          <div className="col align-self-end">       
              <Button 
                className={classes.Searchbuilding2}
                variant="contained" color="primary" disableElevation
                style={{ backgroundColor: '#485D84'}}>
              Search</Button>      
          </div>
          </div> 
        </div>   

    <div>
                        {/*<Billfloorcard />*/}
        <div>
            {bill.map((rows)=>{
            return( 
        <div className="container ">
          <h5 className={classes.headBuilding} id="newannouncetitle"> {rows.buildingName} </h5>
                    {rows.floors.map((f) => { 
                    return(
                    <Paper className={classes.papercard}>
                        <h4 className={classes.headfloors}>Floor{f.floorName}</h4>
                        <div className={classes.headfloor}>  
                            <div style={{ position: 'absolute', paddingTop: '6px' }}>
                                <div className={classes.floortext}>
                                    Room
                                    <div className={classes.minitext} style={{paddingLeft:'1px'}}>  </div>
                                </div>
                                <div className={classes.floortext}>
                                    Bill period
                                    <div className={classes.minitext} style={{paddingLeft:'1px'}}>  </div>
                                </div>
                                <div className={classes.floortext}>
                                    Rent      
                                    <div className={classes.minitext} style={{paddingLeft:'275px'}}>(THB) </div>
                                </div>
                                <div className={classes.floortext}>
                                    Electricity
                                </div>
                                <div className={classes.floortext}>
                                    Water                
                                </div>
                                <div className={classes.floortext}>
                                    Others             
                                </div>
                                <div className={classes.floortext}>
                                    Total            
                                </div>
                                <div className={classes.floortext}>
                                    Status                 
                                </div>
                                <div className={classes.floortext}>
                                    Detail
                                    <div className={classes.minitext} style={{paddingLeft:'3px'}}> </div>
                                </div>
                            </div>
                        </div>   
                        <Divider style={{ backgroundColor: "#AAAAAA", marginTop: "50px" }} />
                        {f.rooms.filter(val=>{
                        if (search == ''){
                          return val;
                        }else if (
                          val.roomNumber.toLowerCase().includes(search.toLowerCase()) 
                         ){
                          return val
                        }                   
                      }).map((n) => { 
                        return(
                        <TableBody >
                            {n.expenses.map((x) => { 
                              return(
                              <TableRow>
                              <TableCell align="right" style={{paddingLeft:'48px'}}>{n.roomNumber}</TableCell>   
                              <TableCell align="left"style={{paddingLeft:'74px'}}
                               tickFormatter={dateFormatter} 
                              >
                              {moment(x.billPeriod).format("L")}
                              {/*x.billPeriod*/}

                              </TableCell>
                              <TableCell align="right"style={{paddingLeft:'73px'}}>
                                    <NumberFormat
                                      value={x.rent.toFixed(2)} 
                                      displayType="text" 
                                      thousandSeparator={true}
                                      decimalScale={2} />
                              </TableCell>

                              <TableCell align="right"style={{paddingLeft:'46px'}}>
                                    <NumberFormat
                                      value={x.electricity.toFixed(2)}
                                      displayType="text" 
                                      thousandSeparator={true} 
                                      decimalScale={2} />
                              </TableCell>
                              <TableCell align="right"style={{paddingLeft:'55px'}}>
                                    <NumberFormat
                                      value={x.waterPrice.toFixed(2)}
                                      displayType="text" 
                                      thousandSeparator={true} 
                                      decimalScale={2} />
                              </TableCell>

                              <TableCell align="left"style={{paddingLeft:'75px'}} >
                                    <NumberFormat
                                      value={x.other.toFixed(2)}
                                      displayType="text" 
                                      thousandSeparator={true} 
                                      decimalScale={2} />
                               </TableCell>
                               <TableCell align="right" style={{paddingLeft:'45px'}} >
                                    <NumberFormat
                                      value={x.totalPrice.toFixed(2)}
                                      displayType="text" 
                                      thousandSeparator={true} 
                                      decimalScale={2} />
                                </TableCell>

                                <TableCell align="left"style={{paddingLeft:'62px'}} >
                                  {n.statusInfo}
                              </TableCell>

                              <TableCell style={{paddingLeft:'69px'}} >
                                <Link to={`/billdetails/${n.roomId}`}>
                                    <InfoOutlinedIcon/>
                                </Link>
                              </TableCell>

                              <TableCell></TableCell>

                            </TableRow>  
                            )})}   

                        </TableBody>  
                        )})} 

                    </Paper>
                       )})}              
                </div>      
                )})}    
            </div>                                   
        </div>       
    </div>
</div>
</ScrollView>
)
}
