import React from 'react'
import 'bootstrap/dist/css/bootstrap.min.css'
import {DropdownButton,Dropdown} from 'react-bootstrap'
import DropdownItem from 'react-bootstrap/esm/DropdownItem';


class Arraylist extends React.Component {

    newstype={
        myarray:["Important News", "General News"]
    }

    constructor() {
        super();

        this.state = {
          dropDownValue: "Select an item"
        }
      }

      changeValue(text) {
        this.setState({dropDownValue: text})
      }

    render()
    {
        return(
            <div>
                <select>
                    {this.newstype.myarray.map(data=>(
                        <option title={data}>{data}</option>
                    ))}
                </select>
                <DropdownButton variant="success" title={this.state.dropDownValue}>
                    {/* {this.newstype.myarray.map(data=>(
                        <Dropdown.Item title={data}>{data}</Dropdown.Item>
                    ))} */}
                    <Dropdown.Item as="button"><div onClick={(e) => this.changeValue(e.target.textContent)}>Important News</div></Dropdown.Item>
                    <Dropdown.Item as="button"><div onClick={(e) => this.changeValue(e.target.textContent)}>Item #1</div></Dropdown.Item>

                </DropdownButton>

            </div>
        );
    }
}
export default Arraylist;
