import React, { useState, useEffect } from 'react';
import { makeStyles, useTheme } from '@material-ui/core/styles';

import CssBaseline from '@material-ui/core/CssBaseline';

import Axios from "axios";

import Button from "@material-ui/core/Button";


import Card from '@material-ui/core/Card';
import "../../Css/Building/Building.css"

const drawerWidth = 240;
const useStyles = makeStyles((theme) => ({
    rootBuilding: {
        display: 'flex',
    },

    menuButton: {
        marginRight: 36,
    },

    toolbar: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        padding: theme.spacing(0, 1),
        // necessary for content to be below app bar
        ...theme.mixins.toolbar,
    },
    content: {
        flexGrow: 1,
        padding: theme.spacing(3),
    },
    root: {
        marginLeft: '8%',
        marginTop: "-3%",
        display: "block",
        '& > * + *': {
            marginTop: theme.spacing(2),
        },

        left: {
            marginright: '300%'
        },

        Btn: {
            padding: '10px 170px'
        },
        CraeteButton: {
            width: 120,
        },
    },
    fabButton: {
        width: "60%",
        //left: 120,
        top: 40,
        right: -120,
        margin: '0 auto',
    },
    Card: {
        width: '60%',
        padding: theme.spacing(3),
        margin: 'auto',
        marginRight: '25%'
    },
    fame: {
        width: "755px",
        height: "652px",
        backgroundColor:red

    }
}));


export default function Createbuilding(props) {
    const classes = useStyles();
    const { values, setValue, errors, setErrors, handleInputChange, resetFormControls } = props;
    const [inputText, setInputText] = useState("");
    const [items, setItems] = useState([]);



    const [building, setBuilding] = useState("");
    const [address, setAddress] = useState("");
    const [province, setProvince] = useState("");
    const [subdistric, setSubdistric] = useState("");
    const [district, setDistrict] = useState("");
    const [zipcode, setZipcode] = useState("");
    const [email, setEmail] = useState("");
    const [phone, setPhone] = useState("");

    const Buildingsave = event => {
        event.preventDefault();
        Axios.post("/building/create-building", {

            "Building": {
                "building_name": building
            },
            "Address": address,
            "District": district,
            "Province": province,
            "SubDistrict": subdistric,
            "Zipcode": zipcode,
            "Email": email,
            "PhoneNumber": phone

        }).then((response) => {
            window.location.href = '/residence';
            console.log(response);
        })

    };

    return (

        <div className={classes.root}>
            <CssBaseline />
            <main className={classes.content}>
                <div className={classes.toolbar} />
                <div className="container ">

                </div>
                <Card component="form" className={classes.Card} variant="outlined">
                    <br />
                    <div className="container">
                        <div className="forms">
                            <div className="Building">
                                <label htmlFor="BuildingName">Building Name</label>
                                <input
                                    //className="form-control"
                                    placeholder="Building Name"
                                    type="text"
                                    //name="Building"
                                    onChange={(event) => {
                                        setBuilding(event.target.value);
                                    }}
                                />
                            </div>

                            <div className="password">
                                <label htmlFor="Address">Address</label>
                                <input
                                    placeholder="Address"
                                    type="text"
                                    //name="Address"
                                    onChange={(event) => {
                                        setAddress(event.target.value);
                                    }} />
                            </div>

                            <div className="Province">
                                <label htmlFor="Province">Province</label>
                                <input
                                    placeholder="Province"
                                    type="text"
                                    //name="Province"
                                    onChange={(event) => {
                                        setProvince(event.target.value);
                                    }} />
                            </div>

                            <div className="SubDistric">
                                <label htmlFor="Sub-Distric">Sub-district</label>
                                <input
                                    placeholder="Sub-disdrict"
                                    type="text"
                                    //name="Province"
                                    onChange={(event) => {
                                        setSubdistric(event.target.value);
                                    }} />

                            </div>

                            <div className="District">
                                <label htmlFor="District">District</label>
                                <input
                                    placeholder="Disdrict"
                                    type="text"
                                    //name="Province"
                                    onChange={(event) => {
                                        setDistrict(event.target.value);
                                    }} />
                            </div>

                            <div className="zipcode">
                                <label htmlFor="District">Zipcode</label>
                                <input
                                    placeholder="Zipcode"
                                    type="text"
                                    //name="Zipcode"
                                    onChange={(event) => {
                                        setZipcode(event.target.value);
                                    }} />

                            </div>

                            <div className="Phone">
                                <label htmlFor="Phone">Phone Number</label>
                                <input
                                    placeholder="Phone Number"
                                    type="text"
                                    //name="Phone Number"
                                    onChange={(event) => {
                                        setPhone(event.target.value);
                                    }} />
                            </div>

                            <div className="Phone">
                                <label htmlFor="lastName">Email</label>
                                <input
                                    placeholder="Email"
                                    type="text"
                                    //name="lastName"
                                    onChange={(event) => {
                                        setEmail(event.target.value);
                                    }} />
                            </div>

                        </div>
                        <Button
                            className={classes.fabButton}
                            variant="contained" color="primary" disableElevation
                            onClick={Buildingsave}
                            style={{ backgroundColor: '#485D84' }} >
                            Create
                        </Button>
                    </div>
                </Card>
            </main>
        </div>
    );
}
