import React from 'react'
import { makeStyles } from '@material-ui/core/styles';
import Datetoday from '../../Components/AllComponent/Datetoday.js'
import { ScrollView } from 'react-native';
import { useState } from 'react';
import { Link } from 'react-router-dom';
import Floorcom from './STRoomComp/Floorcom.js';
import axios from 'axios';
import { useEffect } from 'react';
import DisplayDialog from './DisplayDialog.js';
import DropFloor from '../../Components/Dropdown/DropFloor.js';
import Arraylist from '../../Components/Anouncement/Arraylist.js';
import DropBuilding from '../../Components/Dropdown/DropFloor.js';
import DropStatus from '../../Components/Dropdown/DropStatus.js';
import { Button } from '@material-ui/core';
import DropIsAvailable from '../../Components/Dropdown/DropIsAvailable.js';


const useStyles = makeStyles((theme) => ({
    frame: {
        width: '1163px',
        height: '232px',
        backgroundColor: 'red',
        position: "relative"
    },

    scrollspace: {
        height: "730px",
        width: '1163px',
        margin: 'auto',
        paddingLeft: 110,
        transition: 'all 0.5s ease'

    },
    scrollspace36: {
        height: "730px",
        width: '1163px',
        margin: 'auto',
        paddingLeft: 36,
        transition: 'all 0.5s ease'

    },
    texttitle: {
        fontSize: "19px",
        fontWeight: "bold",
        color: "#4A4A4A"
    },
    texttitle: {
        fontSize: "19px",
        fontWeight: "bold",
        color: "#4A4A4A"
    },
    textDrop: {
        fontSize: 16,
        color: "#4A4A4A"

    },
    titleText: {
        fontSize: 19.4,
        fontWeight: 'bold',
        color: "#4A4A4A"

    },
    buttontop: {
        backgroundColor: '#485D84',
        borderRadius: "5px",
        textTransform: "none",
        fontSize: "13px",
        color: '#fff',
        fontWeight: 'normal',
        width: "107.6px",
        height: "31.5px",
        // position: "absolute",

        '&:hover': {
            backgroundColor: '#687690',
            color: '#fff',
            width: "107.6px",
            height: "31.5px"
        },
    }
}));

export default function STRoompage({ isOpened }) {
    const classes = useStyles();
    useEffect(() => {
        axios('/room/room-types/King Solomon')
            .then(response => {
                console.log(response.data)
                setAllFloor(response.data);
            })
            .catch(error => {
                console.log('Error getting fake data: ' + error);
            })
    }, []);
    const [allFloor, setAllFloor] = useState([]);
    const [buttonPopup, setButtonPopup] = useState(false)


    const [dropFloorSelect, setDropFloorSelect] = useState("2")

    function findpositionElement() {
        for (var i = 0; i < allFloor.length; i++) {
            if (allFloor[i].floorName == dropFloorSelect) {
                return i
            }

        }
    }



    return (
        <div style={{ width: "100%", height: "100%" }}>
            <DisplayDialog
                trigger={buttonPopup}
                setTrigger={setButtonPopup} />

            <ScrollView
            // scrollEnabled={!buttonPopup}
            >
                <div className={isOpened ? classes.scrollspace36 : classes.scrollspace}>
                    <div>
                        <div className={classes.frame}>
                        <Datetoday />
                        {/* <Button  style={{backgroundColor: 'blue'}} onClick={() => props.isOpened('Anna')}/> */}
                        <div className={classes.titleText}>
                            Expense History
                        </div>

                        <div style={{ display: "flex", position: "absolute", bottom: 70, width: '100%' }}>


                            <div style={{ position: 'relative', width: 161 }}>
                                <div className={classes.textDrop}>
                                    Search
                                </div>
                                <div style={{ height: 4 }} />

                                <input placeholder="" type="text" style={{ backgroundColor: 'white', height: 31.5, width: 161, fontSize: 16, color: '#4A4A4A', position: 'absolute' }}>

                                </input>
                            </div>

                            <div style={{ width: 12 }} />

                            <div>
                                <div className={classes.textDrop}>
                                    Building
                                </div>
                                <div style={{ height: 4 }} />

                                <DropBuilding />
                            </div>

                            <div style={{ width: 12 }} />

                            <div>
                                <div className={classes.textDrop}>
                                    Floor
                                </div>
                                <div style={{ height: 4 }} />

                                <DropFloor
                                    url='/filter/filter-building/King David'
                                />
                            </div>

                            <div style={{ width: 12 }} />


                            <div>
                                <div className={classes.textDrop}>
                                    Status
                                </div>
                                <div style={{ height: 4 }} />

                                <DropStatus />
                            </div>

                            <div style={{ width: 12 }} />

                            <div>
                                <div className={classes.textDrop}>
                                    Date
                                </div>
                                <div style={{ height: 4 }} />

                                <input
                                    className={classes.textDrop}
                                    placeholder="Date"
                                    type="date"
                                    name="Date"
                                    noValidate
                                    style={{ backgroundColor: 'white', height: 31.5, width: 161, fontSize: 16, color: '#4A4A4A', position: 'absolute' }}
                                // onChange={(event) => {
                                //     setDatecreate(event.target.value)
                                // }}
                                />
                            </div>

                            <div style={{ position: 'absolute', right: 0, top: 28 }}>
                                <Button className={classes.buttontop} >
                                    Search
                                </Button>
                            </div>

                        </div>


                        <div className={classes.titleText} style={{ position: 'absolute', bottom: 0 }}>
                            Building
                        </div>

                    </div>
                        <div>
                            {/* {allFloor.filter(floor => floor.floorName == '2').map((item, index) => ( */}
                            {true ?
                                allFloor.map((item, index) => (

                                    <Floorcom
                                        // key={index}
                                        // getcurrentSelect = {currentSelect => setCurrentSelect(currentSelect)}
                                        setTrigger={setButtonPopup}
                                        floorName={item.floorName}
                                        allFloor={allFloor[index].rooms}
                                    />
                                ))
                                :
                                allFloor.filter(floor => floor.floorName == dropFloorSelect).map((item, index) => (
                                    <Floorcom
                                        // key={index}
                                        // getcurrentSelect = {currentSelect => setCurrentSelect(currentSelect)}
                                        setTrigger={setButtonPopup}
                                        floorName={item.floorName}
                                        allFloor={allFloor[findpositionElement()].rooms}
                                    />

                                ))}



                            {/* {allData.map((value, index) => {
                            return value.type === "Important News" ?
                                <ImpNews
                                    key={index}
                                    Name={value.title}
                                    img={value.imageUrl}
                                    //cell={value.description}
                                    Date={value.announceDate}
                                    ID={value.id} />
                                : null

                        })} */}

                        </div>

                    </div>
                </div>
            </ScrollView>
        </div>

    )
}
