function Social() {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);
  const handleClickOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };

  const [allData, setAllData] = useState([]);
  const [filteredData, setFilteredData] = useState(allData);
  const handleSearch = (event) => {
      let value = event.target.value.toLowerCase();
      let result = [];
          console.log(value);
          result = allData.filter((data) => {
          return data.tite.search(value) != -1;
    });
    setFilteredData(result);
    }

useEffect(() => {
    axios('https://60aa459d66f1d000177729b4.mockapi.io/api/v1/announcement')
    .then(response => {
         console.log(response.data)
    setAllData(response.data);
    setFilteredData(response.data);
    })
    .catch(error => {
    console.log('Error getting fake data: ' + error);
    })
    }, []);

return (  
    <div className="row">                
           <div className="col-md">    
        <Paper component="form" className={classes.root}>
        <TextField 
              size="small"
              variant="outlined" 
              label="Search" 
              onChange={(event) =>handleSearch(event)} 
              placeholder="Search..."
            />
      </Paper>
      </div>

    <div className="col-md-2">    
        <div className={classes.Move} >
        <Button variant="contained" color="primary" disableElevation
          style={{ backgroundColor: '#485D84' }}
        onClick={handleClickOpen}>
        Compose
      </Button>   
      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="edit-apartment"
      >
      <DialogTitle id="edit-apartment">
      <h4>  New Announcement </h4> 
        </DialogTitle>
        <DialogContent>
          <DialogContentText>
          </DialogContentText>

    <form noValidate>
          <div className="Title">
                <label >Title</label>
                <input   
                  placeholder="Title"
                  type="Buildingname"
                  name="text"
                  noValidate           
                />    
              </div>

              <div className="Type">
                <label htmlFor="Province">Type</label>
                <input   
                  placeholder="Type"
                  type="Province"
                  name="Province"
                  noValidate           
                />    
              </div>

              <div className="Date">
                <label htmlFor="Date">Date</label>
                <input   
                  placeholder="Date"
                  type="date"
                  name="Date"
                  noValidate           
                />    
              </div>

              <div className="Description">
                <label htmlFor="Date">Description</label>
                <textarea  className="Des"
                  placeholder="Description"
                  type="text"
                  name="Description"
                  noValidate           
                />    
              </div>

            </form>
        </DialogContent>      
        <DialogActions >
            <Button  className={classes.Btn} variant="contained" color="primary" disableElevation
              style={{ backgroundColor: '#485D84' }}
              onClick={handleClickOpen}>
              Announce
            </Button>   
            <Button className={classes.Btn} variant="contained" color="primary" disableElevation
              style={{ backgroundColor: '#485D84' }}
              onClick={handleClickOpen}>
              Cancel
          </Button>   
        </DialogActions>
      </Dialog>
</div>

</div>

        <div className="row">                
           <div className="col-md">           
             <div className="AppBack">                    
                <div className="cards-container">
                 <Typography gutterBottom variant="h5" component="h1">    
                  <h3>Important News</h3>    
                  <Divider variant="middle"/>
                 </Typography>                       
                    {filteredData.map((value,index)=>(                 
                    <ImpNews
                        key={index} 
                        Name={value.tite}
                        img={value.image}
                        //cell={value.description}
                        ID ={value.id}/>
                         ))}                
                    </div>                
                </div>                    
             </div>

            <div className="col-md">
              <div className="AppBack2"> 
                <div className="cards-container">  
                  <Typography gutterBottom variant="h5" component="h1">    
                  <h3 >General News</h3>    
                  <Divider variant="middle"/>                
                 </Typography>                                      

                   <General/>

                 </div>                   
            </div>                
        </div>
    </div>
</div>
);
}

export default Social;

























