import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import ButtonBase from '@material-ui/core/ButtonBase';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
import Button from '@material-ui/core/Button';
import { Divider } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    marginTop: 0,  
  },

  Input:{
    width:"180%",
    marginRight:70
 
  },

  Button:{
    height:"35.6px",
    width:"52.6px"
    },

  paper: {
    // padding: theme.spacing(2),
    width: "436px",
    borderRadius: "8.5px",
    height: "606px"
  },

  image: {
    width: 300,
    height: 308,
  },
  img: {
    margin: 'auto',
    display: 'block',
    maxWidth: '100%',
    maxHeight: '100%',
  },
  heder:{
    height: "105px",
    position: "relative"
    // padding: theme.spacing(1),
    // marginTop: '4%',
    
  },
}));

export default function Comment() {
  const classes = useStyles();
  return (
    <div className={classes.root}>
      <Paper className={classes.paper}>
        <Table >
            <div className={classes.heder} >    
            <div style={{marginLeft:"25px", paddingTop:"10.4px",display: "block" }}>
              <p style={{fontSize:"19.4px", fontWeight:"bold", color:"#4A4A4A",display: "inline", }}>
                Comment
              </p>

                <p style={{fontSize:"19.4px", fontWeight:"bold", color:"#4A4A4A",display: "inline", float:'right',marginRight:'13.7px',marginTop:'22px' }}>

                    100 Likes
                </p>

            </div>

          
            </div>
            <Divider style={{backgroundColor:"#AAAAAA", margin: "0px 13.7px"}}/>
        <Grid container spacing={2}>
          <Grid item>
            
          </Grid>
          <Grid item xs={12} sm container>
            <Grid item xs container direction="column" spacing={2}>
              <Grid item xs>
              <ButtonBase className={classes.image}>
            </ButtonBase>
              </Grid>
                    <div className="row" style={{margin:"0px 0px 0px 0px"}}> 
                      <img style={{width:"35.6px", height:"35.6px", backgroundColor:"#3FBAB1", borderRadius:"17.8px", display:"inline"}}></img>  
                      <input className={classes.Input} style={{width:"296px", height:"35.6px", display:"inline", borderRadius:"17.8px"}}></input>  
                      <Button className={classes.Button} style={{margin:"0px 0px 0px 0px" , display:"inline"}}>send</Button>
                    </div>
                      

            </Grid>        
          </Grid> 
        </Grid>
      </Table>
    </Paper>
  </div>







  );
}
