  papercard: {
      width: '436px',
      boxShadow: "1px 1px 3px #E8E8E8",
    //   margin: 'auto',
      marginLeft: 0,
      borderRadius: "8.5px",
    //   marginBottom: "20px",
      backgroundColor:'green',

