import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import { Divider } from '@material-ui/core';
import { useState, useEffect } from 'react';
import PersonIcon from '@material-ui/icons/Person';
import { Grid } from '@material-ui/core';
import Roomcom from './Roomcom';
import { Button } from '@material-ui/core';
import axios from 'axios';

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
        margin: theme.spacing(1),
        marginRight: 40

    },
    papercard: {
        width: '1163px',
        boxShadow: "0px 0px 0px #E8E8E8",
        minHeight: '233px',
        //   margin: 'auto',
        marginLeft: 0,
        marginBottom: "16px",
        //   backgroundColor:'green',
        position: 'relative',
        borderRadius: 5,
        border: "solid 1px #AAAAAA"
    },
    headfloor: {
        // display: 'block',
        posiotion: 'relative',
        marginLeft: '45px',
        paddingTop: '0px',
        verticalAlign: 'middle'

    },
    floortext: {
        color: '#4A4A4A',
        fontSize: '16px',
        fontWeight: 'bold',
        position: 'absolute',
        marginTop: '28px'
    },
    numinroom: {
        height: "25px",
        width: "84px",
        backgroundColor: '#D8D8D8',
        position: 'absolute',
        marginTop: '28px',
        marginLeft: '86px',
        borderRadius: '13px',
        fontSize: '13px',
        color: '#4A4A4A',
        fontWeight: '400',
        display: 'block'
    },
    personicon: {
        width: '18px',
        height: '18px',
        position: 'absolute',
        top: 4,
        left: 10
    },
    aligninicon: {
        display: 'inline-block',
        marginTop: '3px',
        marginLeft: '30px'
    },
    btblue: {
        fontSize: '13px',
        backgroundColor: "#485D84",
        color: "#FFFFFF",
        textTransform: 'none',
        width: '107px'
    },
    btwhite: {
        fontSize: '13px',
        backgroundColor: "#FFFFFF",
        border: "solid 0.5px #AAAAAA",
        color: "#4A4A4A",
        textTransform: 'none',
        width: '107px'

    }
}));

export default function Floorcom(props) {
    const classes = useStyles();
    const str = props.Date
    const [allRoom, setAllRoom] = useState(props.allFloor);
    const [allSelect, setAllSelect] = useState([]);

    const [keptAS,  setAS] = useState([])
    function handlerClick() {

        // console.log(allSelect)
        keptAS.push(allSelect)
        console.log(keptAS)


    }

    return (
        <div>
            <Paper className={classes.papercard} onClick={()=> handlerClick()}>
                <div className={classes.headfloor}>
                    <div className={classes.floortext}>
                        Floor {props.floorName}
                    </div>
                    <div style={{ display: 'flex', position: 'absolute', top: 28, right: 0 }}>
                        <Button className={classes.btblue}>
                            Select All
                        </Button>
                        <div style={{ width: '11.8px' }} />
                        <Button className={classes.btwhite}>
                            Deselect All
                        </Button>
                        <div style={{ width: '21.8px' }} />

                    </div>
                </div>
                <Divider style={{ backgroundColor: "#AAAAAA", marginTop: 81 }} />
                <div style={{ display: 'flex', flexWrap: 'wrap', marginLeft: '50.5px', marginTop: '32.36px', marginBottom: '21.8px' }}>
                    {allRoom.map((sub, value) => (

                        <Roomcom 
                        save  = {setAllSelect(allSelect)}

                        roomNumber = {sub.roomNumber} 
                        id = {sub.id}
                        // roomTypeName = {sub.roomTypeName}
                        />

                    ))}

                </div>



            </Paper>

        </div>
    );
}
