import React from 'react'
import { makeStyles } from '@material-ui/core/styles';
import Datetoday from '../../Components/AllComponent/Datetoday.js'
import { ScrollView } from 'react-native';
import { useState } from 'react';
import Floorsection from './Roomcomponent/Floorsection';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useEffect } from 'react';
import DropFloor from '../../Components/Dropdown/DropFloor.js';
import { Dropdown } from 'react-bootstrap';
import DropBuilding from '../../Components/Dropdown/DropFloor.js';


const useStyles = makeStyles((theme) => ({
    frame: {
        width: '1163px',
        height: '232px',
        backgroundColor: 'red',
        position: "relative"
    },

    scrollspace: {
        height: "730px",
        width: '1163px',
        margin: 'auto',
        paddingLeft: 110,
        transition: 'all 0.5s ease'

    },
    scrollspace36: {
        height: "730px",
        width: '1163px',
        margin: 'auto',
        paddingLeft: 36,
        transition: 'all 0.5s ease'

    },
    texttitle: {
        fontSize: "19px",
        fontWeight: "bold",
        color: "#4A4A4A"
    }
}));

export default function Roompage({ isOpened }) {
    const classes = useStyles();
    const [allFloor, setAllFloor] = useState([]);

    const [keptDropFloor, setKeptDropFloor] = useState("")
    const [keptDropBuilding, setKeptDropBuilding] = useState("")

    useEffect(() => {
        axios('/room/room-beds/King David')
            .then(response => {
                console.log(response.data)
                setAllFloor(response.data);
            })
            .catch(error => {
                console.log('Error getting fake data: ' + error);
            })
    }, []);

    return (
        <ScrollView>
            <div className={isOpened ? classes.scrollspace36 : classes.scrollspace}>
                <div>

                <div className={classes.frame}>
                        <Datetoday />
                        {/* <Button  style={{backgroundColor: 'blue'}} onClick={() => props.isOpened('Anna')}/> */}
                        <div className={classes.titleText}>
                            Expense History
                        </div>

                        <div style={{ display: "flex", position: "absolute", bottom: 70, width: '100%' }}>


                            <div style={{ position: 'relative', width: 161 }}>
                                <div className={classes.textDrop}>
                                    Search
                                </div>
                                <div style={{ height: 4 }} />

                                <input placeholder="" type="text" style={{ backgroundColor: 'white', height: 31.5, width: 161, fontSize: 16, color: '#4A4A4A', position: 'absolute' }}>

                                </input>
                            </div>

                            <div style={{ width: 12 }} />

                            <div>
                                <div className={classes.textDrop}>
                                    Building
                                </div>
                                <div style={{ height: 4 }} />

                                <DropBuilding />
                            </div>

                            <div style={{ width: 12 }} />

                            <div>
                                <div className={classes.textDrop}>
                                    Floor
                                </div>
                                <div style={{ height: 4 }} />

                                <DropFloor
                                    url='/filter/filter-building/King David'
                                />
                            </div>

                            <div style={{ width: 12 }} />


                            <div>
                                <div className={classes.textDrop}>
                                    Status
                                </div>
                                <div style={{ height: 4 }} />

                                <DropStatus />
                            </div>

                            <div style={{ width: 12 }} />

                            <div>
                                <div className={classes.textDrop}>
                                    Date
                                </div>
                                <div style={{ height: 4 }} />

                                <input
                                    className={classes.textDrop}
                                    placeholder="Date"
                                    type="date"
                                    name="Date"
                                    noValidate
                                    style={{ backgroundColor: 'white', height: 31.5, width: 161, fontSize: 16, color: '#4A4A4A', position: 'absolute' }}
                                // onChange={(event) => {
                                //     setDatecreate(event.target.value)
                                // }}
                                />
                            </div>

                            <div style={{ position: 'absolute', right: 0, top: 28 }}>
                                <Button className={classes.buttontop} >
                                    Search
                                </Button>
                            </div>

                        </div>


                        <div className={classes.titleText} style={{ position: 'absolute', bottom: 0 }}>
                            Building
                        </div>

                    </div>

                    <div>
                        {allFloor.map((item, index) => (

                            <Floorsection
                                floorName={item.floorName}
                                allFloor={allFloor[index].rooms}

                            />

                        ))}
                    </div>
                </div>
            </div>
        </ScrollView>

    )
}
