    title4: {
        fontSize: '13px',
        color: '#4A4A4A',
        fontWeight: 'bold',
        top: 46,
        position: 'absolute'
    },
    title5: {
        fontSize: '13px',
        color: '#4A4A4A',
        position: 'absolute'
    },

    inputbox: {
        position: 'absolute',
        height: "31.5px",
        width: "151px",
        fontSize: '13px',
        fontWeight: '400',
        color: '#4A4A4A',
        marginTop: '14.7px',
        top: 86.6,
        left: 42

