import React from 'react'
import 'bootstrap/dist/css/bootstrap.min.css'
import {DropdownButton,Dropdown} from 'react-bootstrap'
import DropdownItem from 'react-bootstrap/esm/DropdownItem';


class Arraylist extends React.Component {

    newstype={
        myarray:["Important News", "General "]
    }

    render()
    {
        return(
            <div>
                <select>
                    {this.newstype.myarray.map(data=>(
                        <option title={data}>{data}</option>
                    ))}
                </select>
                <DropdownButton variant title="Dropdown button">
                    {this.newstype.myarray.map(data=>(
                        <Dropdown.Item title={data}>{data}

                        </Dropdown.Item>
                    ))}

                </DropdownButton>

            </div>
        );
    }
}
export default Arraylist;
