import React, { useEffect, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import Button from '@material-ui/core/Button';
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import Table from '@material-ui/core/Table';
import TextField from '@material-ui/core/TextField';
import axios from 'axios';
import { ScrollView } from 'react-native';
import { BrowserRouter as Rounter, Route, Link, NavLink, Switch } from 'react-router-dom';


const useStyles = makeStyles((theme) => ({
    root: {
        minWidth: 275,
    },

    heder: {
        width: '100%',
        padding: theme.spacing(1),
        marginTop: '-2%',
    },

    Card: {
        width: '80%',
        padding: theme.spacing(3),
        margin: 'auto',
    },

    Cards: {
        width: '100%',
        padding: theme.spacing(2),
        margin: 'auto',
    },

    bullet: {
        display: 'inline-block',
        margin: '0 2px',
        transform: 'scale(0.8)',
    },

    title: {
        fontSize: 14,
    },

    pos: {
        marginBottom: 12,
    },

    Btn: {
        marginLeft: "92%",
        marginTop: "-6%",
    },
    text: {
        padding: theme.spacing(2, 2, 0),
    },
    paper: {
        paddingBottom: 80,
    },
    list: {
        marginBottom: theme.spacing(2),
    },
    subheader: {
        backgroundColor: theme.palette.background.paper,
    },
    appBar: {
        top: '100%',
        bottom: 0,
    },
    grow: {
        flexGrow: 1,
    },
    fabButton: {
        position: 'absolute',
        width: "30%",
        top: 10,
        left: 0,
        right: 0,
        margin: '0 auto',
    },
    buttonsubmit: {
        width: "407px",
        height: "42.8px",
        position: "absolute",
        top: 620,
        left: "35%"
    },

}));

export default function CreateRoom(props) {
    const classes = useStyles();
    const bull = <span className={classes.bullet}>â€¢</span>;
    const [allroom, setAllroom] = useState([]);
    const [inputRoomToAdd, setInputRoomToAdd] = useState(null)


    useEffect(() => {
        axios('/building/rooms/1')
            .then(response => {
                console.log("hi" + response.data)
                setAllroom(response.data);
            })
            .catch(error => {
                console.log('Error getting fake data: ' + error);
            })
    }, []);



    function handlerClick(){
        const a = 100
        // inputRoomToAdd
        // allroom
        const roomObject = {
            "room_number": "101"
        }


        for (var i = 0; i < inputfloorToAdd; i++) {


            const sampleObject = {
                "FloorName": `${i + 1}`,
                "FloorNumber": i + 1,
                "Rooms": [],

            }


            keptSample.push(sampleObject)

        }




    }

    return (

        <div className="container ">
            <div>
                <ScrollView>
                    <div style={{ width: '100%', height: '550px' }}>
                        <button onClick={() => console.log("hi")}>

                        </button>
                        {allroom.map((set) => {
                            return (
                                <Card className={classes.Card} variant="outlined">
                                    <Table aria-label="caption table">
                                        <TableHead >
                                            <TableRow>
                                                <TableCell className={classes.heder}>
                                                    <h4>Rooms</h4>
                                                    <input
                                                        placeholder="No of Room"
                                                        type="number"
                                                        // value={inputfloorToAdd}
                                                        onChange={(e) => setInputRoomToAdd(parseInt(e.currentTarget.value))}
                                                    />

                                                    <Button className={classes.buttongenerate}
                                                        size="small" variant="contained" color="primary" disableElevation
                                                        style={{ backgroundColor: '#485D84' }}
                                                        onClick={() => {
                                                            handlerClick()
                                                        }}
                                                        >Generate Floor</Button>
                                                </TableCell>


                                            </TableRow>
                                        </TableHead>
                                        <TableBody>

                                            <div className="container">
                                                <h4>{set.building}</h4>
                                                <Card className={classes.Cards} variant="outlined">
                                                    <h5 className={classes.heder}>{set.FloorName}</h5>
                                                    <Table>
                                                        <TableHead >
                                                            <h5 className={classes.heder}>Floor{set.FloorNumber}</h5>

                                                            <TableRow>
                                                                <TableCell>Rooms</TableCell>
                                                                <TableCell align="center">Edit Name Rooms</TableCell>
                                                                <TableCell>Delete</TableCell>
                                                            </TableRow>

                                                        </TableHead>
                                                        {set.room.map((r) => {
                                                            return (
                                                                <TableBody>
                                                                    <TableRow >
                                                                        <TableCell align="left">{r.roomNumber}</TableCell>
                                                                        <TableCell align="center">
                                                                            <TextField
                                                                                size="small"
                                                                                variant="outlined"
                                                                            />
                                                                        </TableCell>
                                                                        <TableCell align="left">
                                                                            <Button>Delete </Button>
                                                                        </TableCell>
                                                                    </TableRow>
                                                                </TableBody>
                                                            )
                                                        })}
                                                    </Table>
                                                </Card>
                                            </div>
                                        </TableBody>
                                    </Table>
                                </Card>
                            )
                        })}
                    </div>
                </ScrollView>
                <div>
                </div>
            </div>

            <Link to="/initial">
                <Button
                    className={classes.buttonsubmit}
                    variant="contained" color="primary" disableElevation
                    style={{ backgroundColor: '#485D84' }} >
                    Save
                </Button>
            </Link>

        </div>



    );


}
