import React from 'react'
import { makeStyles } from '@material-ui/core/styles';
import Datetoday from '../../Components/AllComponent/Datetoday.js'
import { ScrollView } from 'react-native';
import { useState } from 'react';
import Floorsection from './Roomcomponent/Floorsection';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useEffect } from 'react';
import DropFloor from '../../Components/Dropdown/DropFloor.js';
import { Dropdown } from 'react-bootstrap';


const useStyles = makeStyles((theme) => ({
    frame: {
        width: '1163px',
        height: '232px',
        backgroundColor: 'red',
        position: "relative"
    },

    scrollspace: {
        height: "730px",
        width: '1163px',
        margin: 'auto',
        paddingLeft: 110,
        transition: 'all 0.5s ease'

    },
    scrollspace36: {
        height: "730px",
        width: '1163px',
        margin: 'auto',
        paddingLeft: 36,
        transition: 'all 0.5s ease'

    },
    texttitle: {
        fontSize: "19px",
        fontWeight: "bold",
        color: "#4A4A4A"
    }
}));

export default function Roompage({ isOpened }) {
    const classes = useStyles();
    const [allFloor, setAllFloor] = useState([]);

    useEffect(() => {
        axios('/room/room-beds/King David')
            .then(response => {
                console.log(response.data)
                setAllFloor(response.data);
            })
            .catch(error => {
                console.log('Error getting fake data: ' + error);
            })
    }, []);

    return (
        <ScrollView>
            <div className={isOpened ? classes.scrollspace36 : classes.scrollspace}>
                <div>
                    <div className={classes.frame}>
                        <Datetoday />
                        <DropFloor/>
                        <div className={classes.texttitle} style={{ position: 'absolute', bottom: 19 }}>
                            {/* {allFloor[0].buildingName} */}
                        </div>
                    </div>

                    <div>
                        {allFloor.map((item, index) => (

                            <Floorsection
                                floorName={item.floorName}
                                allFloor={allFloor[index].rooms}
                            />
                        ))}
                    </div>
                </div>
            </div>
        </ScrollView>

    )
}
