import React from 'react'
import { makeStyles } from '@material-ui/core/styles';
import Datetoday from '../../Components/AllComponent/Datetoday.js'
import { ScrollView } from 'react-native';
import { useState } from 'react';
import Floorsection from './Roomcomponent/Floorsection';
import { Link } from 'react-router-dom';
import { Divider, Paper } from '@material-ui/core';
import { blue } from '@material-ui/core/colors';
import { useEffect } from 'react';
import axios from 'axios';
import { Button } from '@material-ui/core';
// import SegmentedControl from 'rn-segmented-control';
// import { SegmentedControl } from 'segmented-control'
import SegmentedControl from '@react-native-segmented-control/segmented-control';




const useStyles = makeStyles((theme) => ({
    frame: {
        width: '1163px',
        height: '105px',
    },

    scrollspace: {
        height: "730px",
        width: '1163px',
        margin: 'auto',
        paddingLeft: 110,
        transition: 'all 0.5s ease'

    },
    scrollspace36: {
        height: "730px",
        width: '1163px',
        margin: 'auto',
        paddingLeft: 36,
        transition: 'all 0.5s ease'

    },
    paper: {
        width: '1163px',
        height: '494px',
        boxShadow: "0px 0px 0px #E8E8E8",
        borderRadius: 5,
        border: "1px solid #AAAAAA",
        position: 'relative'
    },
    fameinfo: {
        width: 670,
        height: 310,
        display: 'absolute',
        float: 'right',
        marginTop: '28px',
        marginRight: '127px'
    },
    famerow: {
        height: "59.86px",
        width: "100%",
        display: "flex",
        position: "relative"

    },
    grab: {
        height: "9.7px",
        width: "100%",
        backgroundColor: "white"
    },
    famerow2: {
        height: "31.55px",
        width: "100%",
        position: 'relative',
        display: "flex"

    },
    input_md: {
        height: "31.551px",
        width: "100%",
        fontSize: "16px",
        color: "#4A4A4A",
        border: "1px solid #D3D3D3",
        marginTop: 24,
        borderRadius: 4
    },
    input_sm: {
        width: "156.13px",
        height: "100%",
        fontSize: "16px",
        color: "#4A4A4A",
        border: "1px solid #D3D3D3",
        marginTop: 24,
        borderRadius: 4

    },
    input_full: {
        width: "100%",
        height: "31.5px",
        fontSize: "16px",
        color: "#4A4A4A",
        postion: "absolute",
        marginTop: 28.5,
        border: "1px solid #D3D3D3",
        marginTop: 24,
        borderRadius: 4
    },
    grab4: {
        width: "16.18px"
    },
    block_md: {
        width: "327.645px",
        height: "100%",
    },
    headname: {
        fontSize: "16px",
        color: "#4A4A4A",
        position: "absolute"
    },
    block_sm2: {
        width: "155.429px",
        height: "100%",

    },
    img: {
        position: "absolute",
        width: 138,
        height: 162,
        borderRadius: 5,
        top: 140,
        left: 112
    },
    titleText: {
        fontSize: 19.4,
        fontWeight: 'bold',
        color: "#4A4A4A"

    },
    btmBelow: {
        width: 406,
        height: 42.8,
        backgroundColor: "#485D84",
        textTransform: "none",
        color: '#fff',
    }

}));

export default function Personalinfopage({ isOpened }) {
    const classes = useStyles()


    const [name, setName] = useState("")


    const [allData, setAllData] = useState([])

    //groupsubmit//
    const [addFirstName, setAddFirstName] = useState("")
    const [addLastName, setAddLastName] = useState("")
    const [addDateBirth, setAddDateBirth] = useState("")

    useEffect(() => {
        axios('/user/user-room/1')
            .then(response => {
                console.log(response.data)
                setAllData(response.data);

            })
            .catch(error => {
                console.log('Error getting fake data: ' + error);
