    baseURL: '/announcement/post-announcement'
  })




function Social() {

  const classes2 = useStyles2();
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);
  const handleClickOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };


  const [allData, setAllData] = useState([]);
  const [filteredData, setFilteredData] = useState(allData);
  const handleSearch = (event) => {
    let value = event.target.value.toLowerCase();
    let result = [];
    console.log(value);
    result = allData.filter((data) => {
      return data.tite.search(value) != -1;
    });
    setFilteredData(result);
  }

  useEffect(() => {
    // axios('https://60aa459d66f1d000177729b4.mockapi.io/api/v1/announcement')
    axios('/announcement/announcements')
      .then(response => {
        console.log("hi"+response.data)
        setAllData(response.data);
        setFilteredData(response.data);
      })
      .catch(error => {
        console.log('Error getting fake data: ' + error);
      })
  }, []);
  // onClick={this.createNew}
  const addNews = async () => {
   let res = await api.post('/', {
      "Title": "Great Test2",
      "TypeId": 2,
      "AnnounceDate": "2021-07-20",
      "Description":"A group of Western countries had accused China of hacking Microsoft Exchange - a popular email platform used by companies worldwide. The joint statement accused the Chinese Ministry of State Security (MSS) of undermining global stability and security. China has always maintained that it opposes all forms of cyber-crime. On Monday, New Zealand joined the group of countries including the UK, US and Australia in blaming Chinese state-sponsored actors for malicious cyber activity",
      "ImageUrl":"https://cdn.wallpapersafari.com/36/96/7cRSqV.png",
      "Likes": 3,
      "Comments": 24,
      "StaffId": 2
    })
  }


  return (
    <div>



      <div className="row" style={{marginLeft: '-15px', marginRight: '0px', border:'none', mar}}>
        <Datetoday/>



      </div>



      {/* <div className="col-md-2"> */}
        {/* <div className={classes.Move} > */}
          {/* <Button variant="contained" color="primary" disableElevation
          style={{ backgroundColor: '#485D84' }}
        onClick={handleClickOpen}>
        Compose
      </Button>    */}
          <Dialog 


           classes={{paper: classes2.dialog}}
            open={open}
            onClose={handleClose}
            aria-labelledby="edit-apartment"
          >
            {/* <DialogTitle id="edit-apartment"> */}
            <h5 id="newannouncetitle">  New Announcement </h5>
            {/* </DialogTitle> */}
            <div className="divider"> </div>

            <DialogContent>
              <DialogContentText>
              </DialogContentText>
              <div className="movetotop">
              <form noValidate>
                <div>
                  <p>
                    <label id="titletitle">Title</label>
                    <div className="spacing12"></div>
                    <input
                      className="titleinput"
                      placeholder="Title"
                      type="Buildingname"
                      name="text"
                      noValidate
                    />
                  </p>
                </div>

                <div >
                   <form>
                    <label id="typetitle" htmlFor="Province">Type</label>
                    <div className="spacing9"></div>
                    <Arraylist/>
                   </form>


                </div>
                <div className="spacing12"></div>

                <div >
                  <p>
                    <label id="datetitle" htmlFor="Date">Date</label>
                    <div className="spacing12"></div>
                    <input
                      className="dateinput"
                      placeholder="Date"
                      type="date"
                      name="Date"
                      noValidate
                    />
                  </p>
                </div>

                <div className="Description">
                  <label id="descriptiontitle" htmlFor="Date">Description</label>

                  <textarea className="Des"
                    placeholder="Description"
                    type="text"
                    name="Description"
                    noValidate
                  />
                </div>

              </form>
              </div>

            </DialogContent>
            <DialogActions2 >
              <Button id="announceBT" className={classes.Btn} variant="contained" color="primary" disableElevation

                onClick={addNews}>
                <p id="textAnnounceBt"> Announce</p>
              </Button>
              <div className="spacing"/>
              <Button id="cancelBT" className={classes.Btn} variant="contained" color="primary" disableElevation

                onClick={handleClickOpen}>
                <p id="cancelAnnounceBt">Cancel</p>
              </Button>
            </DialogActions2>
          </Dialog>
        {/* </div> */}
        <div>

        </div>
      {/* </div> */}

      <Grid className="row colorG grab">
        <div className="colorG">
          <div className="newH">
            <h5 id="announcementheader" className="headAnnounce" >
              Announcement
            </h5>
          </div>
          {/* <div className="AppBack">                     */}
          <div className="greatcards-container">
            {/* <div className="row">

                  </div> */}
            <Typography gutterBottom variant="h5" component="h1">
              <h5 id="importanttitle">Important News</h5>
              <div className="divider" />
            </Typography>
            {filteredData.map((value, index) => {
              return value.type === "Important News" ? 
                <ImpNews
                key={index}
                Name={value.title}
                img={value.imageUrl}
                //cell={value.description}
                Date={value.announceDate}
                ID={value.id} />
            : null

            })}
          </div>
        </div>
        {/* </div> */}

        <div className="colorG">
          <div className="newH" >

            <Button id="Buttoncompose" className="customButton" variant="contained" color="primary" disableElevation
              style={{ backgroundColor: '#485D84' }}
              onClick={handleClickOpen}>
              <p id="editfontbutton" className="mediumfont">Compose</p>
            </Button>
          </div>

          {/* <div className="AppBack2">  */}
          <div className="greatcards-container2">

            <Typography gutterBottom variant="h5" component="h1">
              <h5 id="generaltitle">General News</h5>
              <div className="divider" />
            </Typography>
            {filteredData.map((value, index) => {
              return value.type !== "Important News" ? 

              <General
                key={index} 
                Name={value.title}
              //cell={value.description}
                img={value.imageUrl}
                Date={value.announceDate}
              //ID ={index.id}             
              />

            :null
            })}
          </div>
        </div>
        {/* </div> */}
      </Grid>
    </div>
  );
}

