import React from 'react';
import clsx from 'clsx';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import List from '@material-ui/core/List';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
import Grid from '@material-ui/core/Grid';
import Roomcard from "../../Components/Room/Roomcard";
import Card from '@material-ui/core/Card';
import Paper from '@material-ui/core/Paper';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';



const drawerWidth = 240;
const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex', 
    marginTop: '-6%'   
  },

  Cards:{
    width: '100%',
    padding: theme.spacing(2),
    marginTop: "-7%",
    margin: 'auto',
    height: "120%"
  },

  heder:{
    width: '100%',
    padding: theme.spacing(2),
    margin: '50%',

  },

  container: {
    maxHeight:440,
    width: '100%'
  },

  toolbar: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-end',
    padding: theme.spacing(0, 15),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(1),

  },

 Searchstyle: {
    backgroundColor: "#fff",
    borderRadius: '8px',
    width: 200,
    marginBottom: 40,
    marginTop: -24,
    '& > * + *': {
      marginTop: theme.spacing(2),
    },
  },

  Field:{
    marginTop: "2%"
  },

  Heading:{
    marginTop: "3%"
  },

  Btn:{
    marginLeft: "77%",
    marginTop: "-3%"

  }

}));

export default function MiniDrawer() {
  const classes = useStyles();
  const theme = useTheme();
  const [open, setOpen] = React.useState(false);
  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  };
  return (
    <div className={classes.root}>
      <CssBaseline />     
        <main className={classes.content}>
          <div className={classes.toolbar} />
          <Typography paragraph className={classes.Heading} >
            <div class="container d-flex-end">
                <h4>Rooms</h4>
            </div>
          </Typography> 

    <div class="container d-flex-end">       
      <div className="row">                
        <div className="col-md-2">    
        <Paper component="form" className={classes.Searchstyle}>
          <TextField className={classes.Field}
              size="small"
              variant="outlined" 
              label="Search"     
              placeholder="Search..."
            />
        </Paper>
      </div>

      <div className="col-md-2">    
        <Paper component="form" className={classes.Searchstyle}>
        <TextField className={classes.Field}
              size="small"
              variant="outlined" 
              label="Building"     
              placeholder="Building..."
            />
      </Paper>
      </div>

      <div className="col-md-2">    
        <Paper component="form" className={classes.Searchstyle}>
        <TextField className={classes.Field}
              size="small"
              variant="outlined" 
              label="Floor"     
              placeholder="Floor..."
            />
      </Paper>
      </div>

      <div className="col-md-2">    
        <Paper component="form" className={classes.Searchstyle}>
        <TextField className={classes.Field}
              size="small"
              variant="outlined" 
              label="Status"     
              placeholder="Status..."
            />
      </Paper>
      </div>

      <div className="col">    
        <Button className={classes.Btn}
        variant="contained" color="primary" disableElevation
        style={{ backgroundColor: '#485D84' }}>Search</Button>
      </div>
    </div> 
  </div>

    <div class="container d-flex-end">        
      <Card className={classes.Cards} variant="outlined">
        <Table >
         <TableHead className={classes.heder}>
            <TableRow >     
                <TableCell className={classes.heder}>
                  <h4>Floor 1</h4>
                </TableCell>
            </TableRow>
          </TableHead>
        <TableBody>
     <br/>
     <Grid container spacing={2}>
        <Grid item xs={3}>
                    <div className="col-md">
                    <Roomcard
                    Roomname="101"
                    Type="Standard"/>           
                </div>
        </Grid>
        <Grid item xs={3}>
                    <div className="col-md">
                    <Roomcard
                    Roomname="102"
                    Type="Standard"/>   
                </div>
        </Grid>

        <Grid item xs={3}>
                    <div className="col-md">
                    <Roomcard
                    Roomname="103"
                    Type="Standard"/>   
                </div>
        </Grid>
        <Grid item xs={3}>
                    <div className="col-md">
                    <Roomcard
                    Roomname="104"
                    Type="Standard"/>       
                </div>
        </Grid>

        <Grid item xs={3}>
                    <div className="col-md">
                    <Roomcard
                    Roomname="105"
                    Type="Standard"/>         
                </div>
        </Grid>
        <Grid item xs={3}>
                    <div className="col-md">
                    <Roomcard
                    Roomname="106"
                    Type="Standard"/>   
                </div>
        </Grid>
      </Grid>        
          </TableBody>
        </Table> 
    </Card>       
      </div> 
      </main>
</div>
  );
}
