import React, {useState, useEffect} from 'react';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import { Button } from '@material-ui/core';
import { Link } from 'react-router-dom';
import { line } from 'cli-spinners';
import Axios from "axios";


const useStyles = makeStyles((theme) => ({
    fame: {
        width: "755px",
        height: "653px",
        backgroundColor: "red"
    },
    titleframe: {
        height: "85.7px",
        width: "100%",
        backgroundColor: "blue",
        position: "relative"
    },
    mainframe: {
        height: "567.3px",
        width: "100%",
        backgroundColor: "green"
    },
    textbuilding: {
        fontSize: "22.6px",
        fontWeight: "bold",
        color: "#4A4A4A",
        paddingLeft:40,
        paddingTop: 22

    },
    line:{
        height: "1px",
        width: "90%",
        backgroundColor: "#D8D8D8",
        top: 30,
        position: "relative",
        margin: "auto"
    }

}));

export default function Createbuilding(props) {

    const classes = useStyles()
    const {values, setValue, errors, setErrors, handleInputChange, resetFormControls} = props;
    const [inputText, setInputText] = useState("");
    const [items, setItems] = useState([]);



    const [building, setBuilding] = useState("");
    const [address, setAddress] = useState("");
    const [province, setProvince] = useState("");
    const [subdistric, setSubdistric] = useState("");
    const [district, setDistrict] = useState("");
    const [zipcode, setZipcode] = useState("");
    const [email, setEmail] = useState("");
    const [phone, setPhone] = useState("");

    const Buildingsave = event=> {
        event.preventDefault();
        Axios.post("/building/create-building", {

            "Building":{
              "building_name": building
            },
            "Address": address,
            "District": district,
            "Province": province,
            "SubDistrict": subdistric,
            "Zipcode": zipcode,
            "Email": email,
            "PhoneNumber": phone     

        }).then((response)=>{
          window.location.href = '/residence';
          console.log(response);
        })

    };

    return (
        <div className={classes.fame}>
            <div className={classes.titleframe}>
                <div className= {classes.textbuilding}>
                    Create Building
                </div>
                <div className={classes.line}/>
            </div>

            <div className={classes.mainframe}>

            </div>

        </div>
    )
}
