import React from 'react'
import { makeStyles } from '@material-ui/core/styles';
import Datetoday from '../../Components/AllComponent/Datetoday.js'
import { ScrollView } from 'react-native';
import { useState } from 'react';
import { Link } from 'react-router-dom';
import Floorcom from './STRoomComp/Floorcom.js';
import axios from 'axios';
import { useEffect } from 'react';
import DisplayDialog from './DisplayDialog.js';

const useStyles = makeStyles((theme) => ({
    frame: {
        width: '1163px',
        height: '232px',
        backgroundColor: 'red',
        position: "relative"
    },

    scrollspace: {
        height: "730px",
        width: '1163px',
        margin: 'auto',
        paddingLeft: 110,
        transition: 'all 0.5s ease'

    },
    scrollspace36: {
        height: "730px",
        width: '1163px',
        margin: 'auto',
        paddingLeft: 36,
        transition: 'all 0.5s ease'

    },
    texttitle: {
        fontSize: "19px",
        fontWeight: "bold",
        color: "#4A4A4A"
    }
}));

export default function STRoompage({ isOpened }) {
    const classes = useStyles();
    useEffect(() => {
        axios('/building/rooms')
            .then(response => {
                console.log(response.data)
                setAllFloor(response.data);
            })
            .catch(error => {
                console.log('Error getting fake data: ' + error);
            })
    }, []);
    const [allFloor, setAllFloor] = useState([]);
    const [buttonPopup, setButtonPopup] = useState(true)
    const [timePopup, setTimePopup] = useState(false)

    // const [currentSelect, setCurrentSelect] = useState([])
    setTimeout(() => {
        setTimedPopup(true)
    }, 3000)

    return (
        <div style={{width: "100%", height: "100%"}}>
            <DisplayDialog 
            trigger={buttonPopup} 
            // setTrigger={setButtonPopup} 
            setTrigger={setTimePopup}/>

            <ScrollView
            // scrollEnabled={!buttonPopup}
            >
                <div className={isOpened ? classes.scrollspace36 : classes.scrollspace}>
                    <div>
                        <div style={{ height: '58px' }}></div>
                        <div className={classes.frame}>
                            <div className={classes.texttitle}>
                                Room Types
                            </div>

                            <div className={classes.texttitle} style={{ position: 'absolute', bottom: 19 }}>
                                King Solomon
                            </div>
                        </div>
                        <div>
                            {allFloor.map((item, index) => (

                                <Floorcom
                                    // key={index}
                                    // getcurrentSelect = {currentSelect => setCurrentSelect(currentSelect)}
                                    setTrigger={setButtonPopup}
                                    floorName={item.floorName}
                                    allFloor={allFloor[index].room}
                                />
                            ))}

                            {/* {allData.map((value, index) => {
                            return value.type === "Important News" ?
                                <ImpNews
                                    key={index}
                                    Name={value.title}
                                    img={value.imageUrl}
                                    //cell={value.description}
                                    Date={value.announceDate}
                                    ID={value.id} />
                                : null

                        })} */}

                        </div>

                    </div>
                </div>
            </ScrollView>
        </div>

    )
}
