import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import ButtonBase from '@material-ui/core/ButtonBase';
import "./../../Css/Announcement/general.css"
import { CardActionArea } from '@material-ui/core';
import Card from '@material-ui/core/Card';
import moment from 'moment';
import axios from 'axios';


const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    margin: theme.spacing(1),
    marginRight: 40

  },
  paper: {
    padding: theme.spacing(1),
    margin: 'auto',
    maxWidth: 340,
    height: 80,
    borderRadius: 8,
    boxShadow: "1px 1px 3px #E8E8E8",



  },
  image: {
    width: 100,
    height: 63,
    position: 'relative',

  },
  img: {
    // margin: 'auto',
    // display: 'block',
    // maxWidth: '100%',
    // maxHeight: '100%',
    width: 48,
    height: 48,
    objectFit: 'cover',
    borderRadius: 24

  },
  papercard: {
      width: '380px',
      boxShadow: "1px 1px 3px #E8E8E8",
    //   margin: 'auto',
      marginLeft: 0,
      borderRadius: "8.5px",
      marginBottom: "20px",
      backgroundColor:'green'

  },
  typography: {
    fontFamily: "Helvetica Neue",
    color:  "#4A4A4A",
    width: "571px",
    marginLeft: "auto",
    marginRight: "auto",
    display:"block"

  },
}));

export default function Cardcomment(props) {
  const classes = useStyles();
  const str = props.Date




  return (
    <div>
      <Paper className={classes.papercard}>
      {/* <Paper className={classes.paper}> */}
        <Grid container spacing={0}>
          <Grid item>
            {/* <ButtonBase className={classes.img} > */}
              <img className={classes.img} src={props.img} />
            {/* </ButtonBase> */}
          </Grid>
          <Grid item xs={12} sm container>
            <Grid item xs container direction="column" spacing={2}>
              <Grid item xs>
                {/* <Typography gutterBottom variant="subtitle1">  
                </Typography> */}
                {/* <Typography variant="body2" color="textSecondary"> */}
                 <p id="generalcelldate">

                   {/* {props.Date} */}
                   {moment(str.replace(/[^a-zA-Z0-9]/g, "")).format("L")}
                  </p> 
                {/* </Typography> */}
                <Typography className={classes.typography} position="relative">
                 {/* <p id="generalcelltitle" > */}
                     {/* {props.Name}  */}
                     "You keep saying a lot of people died of Covid because of inferior vaccines and blaming it on the government. Is it true? Please don’t look from only one side. You have to think of what the government has done as well — procuring good vaccines that meet standards just like what our neighbours do," he added.
                     {/* </p>  */}
                </Typography>
              </Grid>
              <Grid item>
                <Typography variant="body2" style={{ cursor: 'pointer' }}>
                  {props.remove}
                </Typography>
              </Grid>
            </Grid>
            <Grid item>

            </Grid>
          </Grid>
        </Grid>
      {/* </Paper> */}
      </Paper>
    </div>
  );
}
