import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import { Divider } from '@material-ui/core';
import { useState, useEffect } from 'react';
import PersonIcon from '@material-ui/icons/Person';
import { Grid } from '@material-ui/core';
import Roomcom from './Roomcom';
import { Button } from '@material-ui/core';
import axios from 'axios';


const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
        margin: theme.spacing(1),
        marginRight: 40

    },
    papercard: {
        width: '1163px',
        boxShadow: "0px 0px 0px #E8E8E8",
        minHeight: '233px',
        //   margin: 'auto',
        marginLeft: 0,
        marginBottom: "16px",
        //   backgroundColor:'green',
        position: 'relative',
        borderRadius: 5,
        border: "solid 1px #AAAAAA"
    },
    headfloor: {
        // display: 'block',
        posiotion: 'relative',
        marginLeft: '45px',
        paddingTop: '0px',
        verticalAlign: 'middle'

    },
    floortext: {
        color: '#4A4A4A',
        fontSize: '16px',
        fontWeight: 'bold',
        position: 'absolute',
        marginTop: '28px'
    },
    numinroom: {
        height: "25px",
        width: "84px",
        backgroundColor: '#D8D8D8',
        position: 'absolute',
        marginTop: '28px',
        marginLeft: '86px',
        borderRadius: '13px',
        fontSize: '13px',
        color: '#4A4A4A',
        fontWeight: '400',
        display: 'block'
    },
    personicon: {
        width: '18px',
        height: '18px',
        position: 'absolute',
        top: 4,
        left: 10
    },
    aligninicon: {
        display: 'inline-block',
        marginTop: '3px',
        marginLeft: '30px'
    },
    btblue: {
        fontSize: '13px',
        backgroundColor: "#485D84",
        color: "#FFFFFF",
        textTransform: 'none',
        width: '107px'
    },
    btwhite: {
        fontSize: '13px',
        backgroundColor: "#FFFFFF",
        border: "solid 0.5px #AAAAAA",
        color: "#4A4A4A",
        textTransform: 'none',
        width: '107px'

    },
    btlightblue: {
        fontSize: '13px',
        backgroundColor: "#8396B9",
        color: "#FFFFFF",
        textTransform: 'none',
        width: '107px'
    },
    btgray: {
        fontSize: '13px',
        backgroundColor: "#AAAAAA",
        color: "#FFFFFF",
        textTransform: 'none',
        width: '110px',
        position: 'absolute',
        right: -50,
        // top: "50%",
        top:   'calc(100% - 40px)',
        zIndex: 1


    },


}));

export default function Floorcom(props) {
    const classes = useStyles();
    const str = props.Date
    const [allRoom, setAllRoom] = useState(props.allFloor);
    const [allSelect, setAllSelect] = useState([]);

    const [keptAS, setAS] = useState([])

    function handlerClick() {
        const result = keptAS.filter(word => word == allSelect);
        var index = keptAS.indexOf(allSelect);

        if (result == allSelect) {
            console.log(true);
            if (index > -1) { //Make sure item is present in the array, without if condition, -n indexes will be considered from the end of the array.
                keptAS.splice(index, 1);
            }
            // keptAS.pop(result)

        } else {
            console.log(false);
            keptAS.push(allSelect)
        }
        console.log(keptAS)
        // props.getcurrentSelect(keptAS)



    }



    return (
        <div>
            <Paper className={classes.papercard}
            // onClick={()=> handlerClick()}
            >
                <div className={classes.headfloor}>
                    <div className={classes.floortext}>
                        Floor {props.floorName}
                    </div>
                    <div style={{ display: 'flex', position: 'absolute', top: 28, right: 0 }}>
                        <Button className={classes.btblue} onClick={() => handlerClick()}>
                            Select All
                        </Button>
                        <div style={{ width: '11.8px' }} />
                        <Button className={classes.btwhite} onClick={() => props.setTrigger(true)}>
                            Deselect All
                        </Button>
                        <div style={{ width: '21.8px' }} />

                    </div>
                </div>
                <Divider style={{ backgroundColor: "#AAAAAA", marginTop: 81 }} />
                <div style={{postion: 'absolute'}}>
                    <div className={classes.btgray}>
                        <Button className={classes.btgray}>
                            Set Room Type
                        </Button>
                    </div>
                    <div style={{ height: '32.36px' }}></div>
                    <div style={{ display: 'flex', flexWrap: 'wrap', marginLeft: '50.5px', position: "relative" }}>
                        {allRoom.map((sub, value) => (

                            <div onClick={() => handlerClick()}>
                                <Roomcom

                                    save={allSelect => setAllSelect(allSelect)}
                                    roomNumber={sub.roomNumber}
                                    id={sub.id}
                                // roomTypeName={sub.roomTypeName}
                                />

                            </div>


                        ))}

                    </div>
                    <div style={{ height: '21.8px' }}></div>
                </div>



            </Paper>

        </div>
    );
}
