import React from 'react'
import Exfloorcard from './Exhistorycompo/Exfloorcard'
import { makeStyles } from '@material-ui/core/styles';
import Datetoday from '../../Components/AllComponent/Datetoday.js'
import { ScrollView } from 'react-native';
import Navbar2 from '../../Components/AllComponent/Navbar2';
import { useState } from 'react';
import { Button } from '@material-ui/core';
import axios from 'axios';
import { useEffect } from 'react';
import DropBuilding from '../../Components/Dropdown/DropFloor';
import DropFloor from '../../Components/Dropdown/DropBuilding';
import DropStatus from '../../Components/Dropdown/DropStatus';

const useStyles = makeStyles((theme) => ({
    frame: {
        width: '1163px',
        height: '232px',
        backgroundColor: 'red',
        position: "relative"
    },
    scrollspace: {
        height: "730px",
        width: '1163px',
        margin: 'auto',
        paddingLeft: 110,
        transition: 'all 0.5s ease'

    },
    scrollspace36: {
        height: "730px",
        width: '1163px',
        margin: 'auto',
        paddingLeft: 36,
        transition: 'all 0.5s ease'
    },
    textDrop: {
        fontSize: 16,
        color: "#4A4A4A"

    },
    titleText: {
        fontSize: 19.4,
        fontWeight: 'bold',
        color: "#4A4A4A"

    }
}));

export default function Exhistorypage({ isOpened }) {
    const classes = useStyles();
    const [allFloor, setAllFloor] = useState([])

    useEffect(() => {
        axios('/history/expense-history/King David/date/2021-07-08')
            .then(response => {
                console.log("4444")
                console.log(response.data)
                setAllFloor(response.data);
            })
            .catch(error => {
                console.log('Error getting fake data: ' + error);
            })
    }, []);

    return (

        <ScrollView>
            <div className={isOpened ? classes.scrollspace36 : classes.scrollspace}>
                <div>
                    <div className={classes.frame}>
                        <Datetoday />
                        {/* <Button  style={{backgroundColor: 'blue'}} onClick={() => props.isOpened('Anna')}/> */}
                        <div className={classes.titleText}>
                            Expense History
                        </div>

                        <div style={{ display: "flex", position: "absolute", bottom: 70 }}>


                            <div style={{ position: 'relative', width: 161 }}>
                                <div className={classes.textDrop}>
                                    Search
                                </div>
                                <div style={{ height: 4 }} />

                                <input placeholder="Enter a comment" type="text" style={{ backgroundColor: 'white', height: 31.5, width: 161, fontSize: 16, color: '#4A4A4A', position: 'absolute' }}>

                                </input>
                            </div>

                            <div style={{ width: 12 }} />

                            <div>
                                <div className={classes.textDrop}>
                                    Building
                                </div>
                                <div style={{ height: 4 }} />

                                <DropBuilding />
                            </div>

                            <div style={{ width: 12 }} />

                            <div>
                                <div className={classes.textDrop}>
                                    Floor
                                </div>
                                <div style={{ height: 4 }} />

                                <DropFloor
                                    url='/filter/filter-building/King David'
                                />
                            </div>

                            <div style={{ width: 12 }} />


                            <div>
                                <div className={classes.textDrop}>
                                    Status
                                </div>
                                <div style={{ height: 4 }} />

                                <DropStatus />
                            </div>

                            <div style={{ width: 12 }} />

                            <div>
                                <div className={classes.textDrop}>
                                    Date
                                </div>
                                <div style={{ height: 4 }} />

                                <input

                                    placeholder="Date"
                                    type="date"
                                    name="Date"
                                    noValidate

                                    // onChange={(event) => {
                                    //     setDatecreate(event.target.value)
                                    // }}
                                />
                            </div>
                        </div>






                        <div className={classes.titleText} style={{ position: 'absolute', bottom: 0 }}>
                            Building
                        </div>

                    </div>



                    <div>
                        <div style={{ height: 20 }} />

                        {allFloor.map((item, index) => (

                            <Exfloorcard
                                nowFloor={allFloor[index].floorName}
                                allFloor={allFloor[index].room}



                            />


                        ))}
                        {allFloor.map((item, index) => (

                            <Exfloorcard
                                nowFloor={allFloor[index].floorName}
                                allFloor={allFloor[index].room}



                            />


                        ))}


                    </div>
                </div>
            </div>
        </ScrollView >

    )
}
